/**
 * 天际线描边
 * 支持地形和楼块
 */
export class SkyLine {
    _color: string;
    _width: number;
    _enabled: boolean;
    afterAddToEngine(engine: any): void;
    _engine: any;
    _skylinePass: any;
    set color(arg: string);
    get color(): string;
    set width(arg: number);
    get width(): number;
    set enabled(arg: boolean);
    get enabled(): boolean;
}
